mapper.delete:
DELETE
FROM "user_like"
WHERE user_id = #{userId}
  AND entity_type = #{entityType}
  AND entity_id = #{entityId}

mapper.select:
SELECT
entity_id as "entityId",
true as "isLiked"
FROM "user_like"
WHERE user_id = #{userId}
AND entity_type = #{entityType}
AND entity_id IN

    #{id}

SELECT EXISTS(SELECT 1
              FROM "user_like"
              WHERE user_id = #{userId}
                AND entity_type = #{entityType}
                AND entity_id = #{entityId})
